import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import addButton from '../static/images/plus.svg';
import '../static/css/AddTaskForm.css';
import axios from 'axios';

class AddTaskForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            TaskInput: "",
            TaskList: []
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ name: event.target.value });
    }

    handleSubmit(event) {
        event.preventDefault();
        /*
        const updatedTaskList = ;

        this.setState({TaskList: })

        axios.post(``, { user })
        .then(res => {
            console.log(res);
            console.log(res.data);
        })*/
    }

    componentDidMount() {

        axios.get(`http://localhost:5000/readTable/User`)
        .then(res => {
            const output = res.data[0];
            console.log("Successfully Fetch");

            const tasks = [];

            for (const task in JSON.parse(output.tasks)) {
                tasks.push(JSON.parse(output.tasks)[task]);
            }

            this.setState({TaskList: tasks});

        })


    }

    render() {
    return (
    <div>
    <Container fluid = {true} className = "add-task-container">
        <Row>
            <Col>
                <Container style = {{padding: 0, marginTop: "75px", marginBottom: "75px", backgroundColor: "#F6F6F6", boxShadow: "3px 3px 3px rgba(0, 0, 0, 0.5)"}}>
                    <Row style = {{padding: 0, marginLeft: "0%", marginRight: "0%"}} className = "justify-content-md-center">
                    <Col md = {11} sm = {10} xs = {9} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>
                        <Form.Control placeholder="Add Task" onChange = {this.handleChange} style = {{border: "1.3px solid grey"}} value = {this.state.TaskInput}/>
                    </Col>
                    <Col md = {1} sm = {2} xs = {3} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>
                        <img src = {addButton} className = "add-button" width = "35px" height = "35px" alt = "Add Button"/>
                    </Col>
                    </Row>
                </Container>
            </Col>
        </Row>
    </Container>

    <Container fluid = {true} className = "add-task-container">
        <Row>
            <Col>
                <Container style = {{padding: 0, marginTop: "75px", marginBottom: "75px", backgroundColor: "#F6F6F6", boxShadow: "3px 3px 3px rgba(0, 0, 0, 0.5)"}}>
                    <Row style = {{padding: 0, marginLeft: "0%", marginRight: "0%"}} className = "justify-content-md-center">
                    {this.state.TaskList.map((value, key) => (
                    <Col key = {key} md = {11} sm = {10} xs = {9} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>
                        {value}
                    </Col>
                    ))}
                    </Row>
                </Container>
            </Col>
        </Row>
    </Container>
    </div>
)}}

export default AddTaskForm;
