import React from 'react';
import FacebookLogin from 'react-facebook-login';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Facebook extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoggedIn: false,
            userID: '',
            name: '',
            email: '',
            picture: ''
        }

        this.responseFacebook = this.responseFacebook.bind(this);
        this.componentClicked = this.componentClicked.bind(this);
    }

    responseFacebook(response) {
        this.setState({
            isLoggedIn: true,
            userID: response.userID,
            name: response.name,
            email: response.email,
            picture: response.picture.data.url
        });

        console.log(this.state);
    }

    componentClicked() {
        console.log("clicked");
    }

    render() {
        let fbContent;

        if (this.state.isLoggedIn) {
            fbContent = (
                <Container fluid = {true} style = {{height: "600px"}}>
                  <Row>
                    <Col style = {{display: "flex", justifyContent: "center", textAlign: "center", marginTop: "50px"}}>
                      <p style = {{fontSize: "96px"}}>
                        Hello, {this.state.name}
                      </p>
                    </Col>
                  </Row>
                  <Row>
                      <Col style = {{display: "flex", justifyContent: "center", textAlign: "center", marginTop: "100px"}}>
                      <FacebookLogin
                        appId="571762973627192"
                        autoLoad={true}
                        fields="name,email,picture"
                        onClick={this.componentClicked}
                        callback={this.responseFacebook} />
                      </Col>
                  </Row>
              </Container>);
        } else {
            fbContent = (
            <Container fluid = {true} style = {{height: "600px"}}>
              <Row>
                <Col style = {{display: "flex", justifyContent: "center", textAlign: "center", marginTop: "50px"}}>
                  <p style = {{fontSize: "96px"}}>
                    TO-DO WEB APP
                  </p>
                </Col>
              </Row>
              <Row>
                <Col style = {{display: "flex", justifyContent: "center", textAlign: "center", marginTop: "0px"}}>
                  <h4>
                    Manage Faster, Manage Better
                  </h4>
                </Col>
              </Row>
              <Row>
                  <Col style = {{display: "flex", justifyContent: "center", textAlign: "center", marginTop: "100px"}}>
                  <FacebookLogin
                    appId="571762973627192"
                    autoLoad={true}
                    fields="name,email,picture"
                    onClick={this.componentClicked}
                    callback={this.responseFacebook} />
                  </Col>
              </Row>
          </Container>);
        }

        return (
            <div>
                {fbContent}
            </div>
)}};

export default Facebook;
