import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../static/css/AddTaskForm.css';
import axios from 'axios';

class ToDoList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: '',
            TaskList: []
        };
    }

    componentDidMount() {

        axios.get(`http://localhost:5000/readTable/User`)
        .then(res => {
            const output = res.data[0];
            console.log("Successfully Fetch");

            const tasks = [];

            this.setState({username : output.name});

            const array = Object.keys(output.tasks).map((key) => (output.tasks)[key]);

            console.log(output.tasks);

            for (const task in output.tasks) {
                console.log(task);
                tasks.push(output.tasks[task]);
            }

            this.setState({TaskList: tasks});

            console.log(this.state.username);
            console.log(this.state.TaskList);

        })


    }


    render() {
    return (
<Container fluid = {true} style = {{paddingLeft: "5%", paddingRight: "5%"}}>
    <Row>
        <Col>
            <Container style = {{padding: 0, marginTop: "75px", marginBottom: "75px", backgroundColor: "#F6F6F6", boxShadow: "3px 3px 3px rgba(0, 0, 0, 0.5)"}}>
                <Row style = {{padding: 0, marginLeft: "0%", marginRight: "0%"}} className = "justify-content-md-center">

                <Col md = {11} sm = {10} xs = {9} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>

                </Col>

                </Row>
            </Container>
        </Col>
    </Row>
  </Container>
);}}

export default ToDoList;
