import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import addButton from '../static/images/plus.svg';
import '../static/css/AddTaskForm.css';
import 'axios';

class AddTaskForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            TaskInput: "",
            TaskList: []
        };
    }

    componentDidMount() {
        axios.get(`http://localhost:5000/readTable/User`)
        .then(res => {
            console.log(res.data);
        })
    }

    render() {
    return (
<Container fluid = {true} style = {{paddingLeft: "5%", paddingRight: "5%"}}>
    <Row>
        <Col>
            <Container style = {{padding: 0, marginTop: "75px", marginBottom: "75px", backgroundColor: "#F6F6F6", boxShadow: "2px 3px #202020"}}>
                <Row style = {{padding: 0, marginLeft: "0%", marginRight: "0%"}} className = "justify-content-md-center">
                <Col md = {11} sm = {10} xs = {9} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>
                    <Form.Control placeholder="Add Task" style = {{border: "1.3px solid grey"}} value = {this.state.TaskInput}/>
                </Col>
                <Col md = {1} sm = {2} xs = {3} style = {{display: "flex", justifyContent: "center", textAlign: "center", paddingTop: "15px", paddingBottom: "15px"}}>
                    <img src = {addButton} className = "add-button" width = "35px" height = "35px" alt = "Add Button"/>
                </Col>
                </Row>
            </Container>
        </Col>
    </Row>
  </Container>
);}}

export default AddTaskForm;
